import React, { useState } from "react";
import "./App.css";
import SquareComponent from "./Components/SquareComponent";
import { AppMain, Ptext, Row, Button } from "./style/AppCss";
const initialState = ["", "", "", "", "", "", "", "", ""];

function App() {
  const [gameState, updateGameState] = useState(initialState);
  const [isXChance, updateIsXChance] = useState(false);

  const onSquareClicked = (index) => {
    let strings = Array.from(gameState);
    strings[index] = isXChance ? "X" : "O";
    updateGameState(strings);
    updateIsXChance(isXChance);
  };
  return (
    <div className="App">
      <AppMain>
        <Ptext>Tic Tac Toe Game</Ptext>
        <Row>
          <SquareComponent
            state={gameState[0]}
            onClick={() => onSquareClicked(0)}
          />
          <SquareComponent
            state={gameState[1]}
            onClick={() => onSquareClicked(1)}
          />
          <SquareComponent
            state={gameState[2]}
            onClick={() => onSquareClicked(2)}
          />
        </Row>
        <Row>
          <SquareComponent
            state={gameState[3]}
            onClick={() => onSquareClicked(3)}
          />
          <SquareComponent
            state={gameState[4]}
            onClick={() => onSquareClicked(4)}
          />
          <SquareComponent
            state={gameState[5]}
            onClick={() => onSquareClicked(5)}
          />
        </Row>
        <Row>
          <SquareComponent
            state={gameState[6]}
            onClick={() => onSquareClicked(6)}
          />
          <SquareComponent
            state={gameState[7]}
            onClick={() => onSquareClicked(7)}
          />
          <SquareComponent
            state={gameState[8]}
            onClick={() => onSquareClicked(8)}
          />
        </Row>
        <Button>Clear Game</Button>
      </AppMain>
    </div>
  );
}

export default App;
