import React from "react";
import {DivLogin, HeaderContent ,Input,Button} from './style/login'

class Login extends React.Component{
    state={
        mail:'',
        pwd:''
    }
    handlechange = (e) =>{
        const {name,value} = e.target
        this.setState({[name]:value})
    }
    handleSubmit = (e) =>{
        e.preventDefault()
    }
    render(){
        return(
            <DivLogin>
                <HeaderContent>
                   <h1>Sign In</h1>
                </HeaderContent>

                    <form onSubmit={this.handleSubmit}>
                        <Input type="email" name="mail" placeholder="Enter Your Email" requuired  />
                        <Input type="password" name="pwd" placeholder="Enter Your Password" requuired />
                        <Button onSubmit={this.handleSubmit}>Sign In</Button>
                     </form>

            </DivLogin>

        )
    }
}

export default Login;
