import styled from "styled-components";

export const AppMain = styled.div`
  display: flex;
  min-height: 100vh;
  flex-direction: column;
  text-align: center;
  background-color: #131a28;
  color: white;
  justify-content: center;
  align-items: center;
`;

export const Ptext = styled.p`
  color: white;
  font-size: 42px;
  font-weight: bolder;
  text-align: center;
  margin: 20px;
`;
