import React from "react";
import { DivLogin, HeaderContent, Body, Input, Button } from "./style/login";
//import { useNavigate } from "react-router-dom";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";

const Login = () => {
  const schema = yup
    .object({
      name: yup.string().required(),
      pwd: yup.number().positive().integer().required(),
    })
    .required();
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(schema),
  });
  const onSubmit = (data) => console.log(data);
  //const navigate = useNavigate();
  return (
    <Body>
      <DivLogin>
        <HeaderContent>
          <h1>Log In</h1>
        </HeaderContent>

        {/* <form onSubmit={handleSubmit(onSubmit)}> */}
        <form onSubmit={handleSubmit(onSubmit)}>
          <Input
            type="email"
            name="mail"
            placeholder="Enter your email"
            {...register("mail")}
          />
          <p>{errors.mail?.message}</p>

          <Input
            type="password"
            name="pwd"
            placeholder="Enter Your Password"
            {...register("pwd")}
          />
          <p>{errors.pwd?.message}</p>

          <Input type="submit" />
        </form>
        {/* <Input
            type="email"
            name="mail"
            placeholder="Enter Your Email"
            {...register("mail", {
              required: true,
              minLength: 4,
              maxLength: 20,
            })}
            required
          />
          <Input
