            name="pwd"
            placeholder="Enter Your Password"
            {...register("pwd", {
              required: true,
              minLength: 4,
              maxLength: 20,
            })}
            required
          />
          <Button
            type="submit"
            // onClick={() => {
            //   navigate("/");
            // }}
          >
            Log In
          </Button>
        </form> */}
      </DivLogin>
    </Body>
  );
};

export default Login;
