import styled from "styled-components";

export const DivLogin=styled.div`
width: 400px;
display: flex;
flex-direction: column;
align-items: center;
justify-content:center;
margin: 0 auto;
border: 1px solid#dbdbdb;
margin-top: 30vh;
background-color:rgb(250, 248, 245);
border-radius: 20px;`;

export const HeaderContent=styled.div`
font-size:20px;
font-weight: bolder;
color: dodgerblue;`;


export const Input=styled.input`
width: 80%;
max-width: 100%;
border: 1px solid #dbdbdb;
padding: 15px;
margin: 10px 0;
border-radius: 5px;
background-color: #fafafa;`;

export const Button=styled.button`
width: 88%;
max-width: 100%;
border: 1px solid #dbdbdb;
padding: 15px;
margin: 20px 0;
border-radius: 5px;
cursor: pointer;
background-color: dodgerblue;
font-size: 20px;
color:white;
margin-bottom: 40px;
`;
