import styled from "styled-components";
import Bg from "../images/bg-final.jpeg";

export const Body = styled.div`
  height: 100vh;
  background-image: url(${Bg});
  background-repeat: no-repeat;
  background-size: cover;
  display: flex;
  align-items: center;
  justify-content: center;
`;

export const DivLogin = styled.div`
  width: 400px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  margin: 0 auto;
  border: 1px solid#dbdbdb;
  background-color: rgb(250, 248, 245);
  border-radius: 20px;
`;

export const HeaderContent = styled.div`
  font-size: 20px;
  font-weight: bolder;
  color: rgb(14, 119, 176);
`;

export const Input = styled.input`
  width: 80%;
  max-width: 100%;
  border: 1px solid #dbdbdb;
  padding: 15px;
  margin: 10px 0;
  border-radius: 5px;
  background-color: #fafafa;
  outline: none;
`;

export const Button = styled.button`
  width: 88%;
  max-width: 100%;
  border: 1px solid #dbdbdb;
  padding: 15px;
  margin: 20px 0;
  border-radius: 5px;
  cursor: pointer;
  background-color: rgb(14, 119, 176);
  font-size: 20px;
  color: white;
  margin-bottom: 40px;
`;
